import React,{useContext} from 'react'
import { Link } from 'react-router-dom'

import {BasketContext} from '../../context/basketContext'

const BasketCart = () => {
  const {totalBasketAmount,totalBasketPrice} = useContext(BasketContext)
  return (
    <div className="cart">
      <div className="dropdown">
        <Link to='/basket' id='dLabel' className="btn btn-inverse btn-block btn-lg">
          <i className="fa fa-fa-shopping-cart"></i>
          <span>{totalBasketAmount} item(s) - ${totalBasketPrice}</span>
        </Link>
      </div>
    </div>
  )
}


export default BasketCart
