import React,{useContext,useEffect} from 'react'
import { Link, withRouter, useParams } from 'react-router-dom'
import classNames from 'classnames'

import {MainContext} from '../../context/mainContext'

const Categories = () => {
  const { categories,setActiveGategoryId,renderPhones } = useContext(MainContext)
  let { id: activeCategoryId } = useParams()
  const renderCategory = (category, index) => {
    const linkClass = classNames({
      'list-group-item': true,
      active: activeCategoryId===category.id
    })
    return (
      <Link to={`/categories/${category.id}`} className={linkClass} key={index}>
        {category.name}
      </Link>
    )
  }

  useEffect(() => {
    if (!activeCategoryId) {
      activeCategoryId= 'all'
    }
    setActiveGategoryId(activeCategoryId)
    renderPhones()
  }, [activeCategoryId]);

  const renderAllCategory = () => {
    const linkClass = classNames({
      'list-group-item': true,
      'active': !activeCategoryId
    })
    return (
      <Link to="/" className={linkClass}>
        All
      </Link>
    )
  }
  if (!categories) {
    return null
  }

  return (
    <div className="well">
      <h4>Brand</h4>
      <div className="list-group">
        {renderAllCategory()}
        {categories.map((category, index)=> renderCategory(category, index))}
      </div>
    </div>
  )
}

export default withRouter(Categories)
