import React,{useContext} from 'react'
import { Link } from 'react-router-dom'

import {BasketContext} from '../../context/basketContext'
import 'containers/basket/index.css'


const Basket = () => {
  const { basket,totalBasketPrice, removePhoneFromBasket,cleanBasket, basketCheckout } = useContext(BasketContext)
  const isBasketEmpty = basket.length === 0
  
  const renderContent = () => {
    return (
      <div>
        {isBasketEmpty && <div>Your shopping cart is empty</div>}
        
        <div className="table-responsive">
          <table className="table-bordered table-striped table-condensed cf">
            <tbody>
              {basket.map((phone, index) => (
                <tr key={index} className="item-checout">
                  <td className="first-column-checkout">
                    <img src={phone.image} alt={phone.name} className="img-thumbnail"/>
                  </td>
                  <td>{phone.name}</td>
                  <td>${phone.price}</td>
                  <td>{phone.amount}</td>
                  <td>
                    <span onClick={()=> {removePhoneFromBasket(phone.id)}} className="delete-cart"></span>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      {
        !isBasketEmpty &&
          <div className="row">
            <div className="pull-right total-user-checkout">
              <b>Total:</b>
              ${totalBasketPrice}
            </div>
        </div>
      }
    </div>
    
    )
  }

  const renderSidebar = () => (
    <div>
      <Link
        className='btn btn-info'
        to='/'
      >
        <span className='glyphicon glyphicon-info-sign'></span>
        <span>Continue shopping!</span>
      </Link>
      {
        !isBasketEmpty &&
        <div className="btn-container">
          <button
            onClick={()=>{cleanBasket()}}
            className='btn btn-danger'
          >
            <span className='glyphicon glyphicon-trash'></span>
            Clear cart
          </button>
          <button
            className='btn btn-success'
            onClick={() => {basketCheckout(basket)}}
          >
            <span className='glyphicon glyphicon-envelope'></span>
            Checkout
          </button>
        </div>
      }
    </div>
  )
  return (
    <div className="view-container">
      <div className="container">
        <div className="row">
          <div className="col-md-9">
            {renderContent()}
          </div>
          <div className="col-md-3">
            {renderSidebar()}
          </div>
        </div>
      </div>
    </div>
  )
}



export default Basket
