import {
  FETCH_PHONES_SUCCESS,
  FETCH_PHONES_FAILURE,
  LOAD_MORE_PHONES_SUCCESS,
  LOAD_MORE_PHONES_FAILURE,
  FETCH_CATEGORIES_SUCCESS,
  SET_ACTIVE_CATEGORY_ID,
  FETCH_PHONES_BY_ID_SUCCESS,
  SET_FILTERED_PHONES,
  UPDATE_SEARCH
} from 'actionTypes'

const phones_reducer = (state, {type,payload}) => {
  switch (type) {
    case FETCH_PHONES_SUCCESS:
      return { ...state, phones: payload }
    case FETCH_PHONES_FAILURE:
      console.log(payload);
      return state
    case LOAD_MORE_PHONES_FAILURE:
      console.log(payload);
      return state
    case LOAD_MORE_PHONES_SUCCESS:
      return { ...state, phones: [...state.phones, ...payload] }
    case FETCH_CATEGORIES_SUCCESS:
      return { ...state, categories: payload }
    case SET_ACTIVE_CATEGORY_ID:
      return { ...state, activeCategoryId: payload }
    case SET_FILTERED_PHONES:
      return { ...state, filteredPhones : payload }
    case UPDATE_SEARCH:
      return { ...state, search : payload }

  }
}

export default phones_reducer

